mapper.sql:
EXISTS (
SELECT 1
FROM TB_AGENCY_INFO
WHERE AGENCY_CODE = #{agencyCode}
)
AND EXISTS (
SELECT 1
FROM TB_FLIGHT_INFO
WHERE FLIGHT_CODE = #{flightCode}
)
AND EXISTS (
SELECT 1
FROM TB_HOTEL_INFO
WHERE HOTEL_CODE = #{hotelCode}
)

(
SELECT COUNTRY
FROM TB_AGENCY_INFO
WHERE AGENCY_CODE = #{agencyCode}
) = #{country}
AND (
SELECT COUNTRY
FROM TB_HOTEL_INFO
WHERE HOTEL_CODE = #{hotelCode}
) = #{country}
AND (
SELECT DESTINATION
FROM TB_FLIGHT_INFO
WHERE FLIGHT_CODE = #{flightCode}
) = (
SELECT REGION
FROM TB_AGENCY_INFO
WHERE AGENCY_CODE = #{agencyCode}
)
AND (
SELECT DESTINATION
FROM TB_FLIGHT_INFO
WHERE FLIGHT_CODE = #{flightCode}
) = (
SELECT REGION
FROM TB_HOTEL_INFO
WHERE HOTEL_CODE = #{hotelCode}
)

mapper.update:
UPDATE TB_PACKAGE_INFO
SET IS_USED = 'N'
WHERE
PACKAGE_CODE = #{packageCode}
AND ;

UPDATE TB_PACKAGE_INFO
SET ASSIGN_CODE = #{assignCode}
WHERE
PACKAGE_CODE = #{packageCode}
AND ;
